function showAttList(data){
	if(data.length >0){
		var attTitleHtml = "<h4>附件列表:</h4>";
		$("#newsAttTitle").html(attTitleHtml);
		
		var attHtml = "";
		for(var i=0; i< data.length; i++){
			
			attHtml += "<p><a href='javascript:void(0)' onclick=\"DownloadNewsAtt('"+ data[i].attName +"','"+ data[i].attUrl +"')\">"+ data[i].attName +"</a></p>";
		}
		
		$("#newsAttList").html(attHtml);
	}else{
		//无附件 div隐藏
		$("#newsAttTitle,#newsAttList").hide();
	}

}


